'use strict';

/* Controllers */

  // bootstrap controller
  app.controller('AccordionDemoCtrl', ['$scope', '$rootScope', '$http', '$location','$sanitize','FileUploader','toaster', 
  function($scope, $rootScope, $http, $location,$sanitize,FileUploader,toaster) {



     $scope.toaster = {
        type: 'success',
        title: 'Message',
        text: 'Fichier attachée a bien été ajouté.'
    };
 

     $scope.postType = {
       value :  true
     };


    

     var filename='';
    


var uploader = $scope.uploader = new FileUploader({
            url: 'js/controllers/upload.php'
        });

        // FILTERS
      
        // a sync filter
        uploader.filters.push({
            name: 'syncFilter',
            fn: function(item /*{File|FileLikeObject}*/, options) {
                console.log('syncFilter');
                return this.queue.length < 10;
            }
        });
       
        // CALLBACKS

        uploader.onWhenAddingFileFailed = function(item /*{File|FileLikeObject}*/, filter, options) {
            console.info('onWhenAddingFileFailed', item, filter, options);
        };
        uploader.onAfterAddingFile = function(fileItem) {
            console.info('onAfterAddingFile', fileItem);
        };
        uploader.onAfterAddingAll = function(addedFileItems) {
            console.info('onAfterAddingAll', addedFileItems);
        };
        uploader.onBeforeUploadItem = function(item) {
            console.info('onBeforeUploadItem', item);
        };
        uploader.onProgressItem = function(fileItem, progress) {
            console.info('onProgressItem', fileItem, progress);
            filename=fileItem.file.name;
        };
        uploader.onProgressAll = function(progress) {
            console.info('onProgressAll', progress);
        };
        uploader.onSuccessItem = function(fileItem, response, status, headers) {
            console.info('onSuccessItem', fileItem, response, status, headers);
        };
        uploader.onErrorItem = function(fileItem, response, status, headers) {
            console.info('onErrorItem', fileItem, response, status, headers);
        };
        uploader.onCancelItem = function(fileItem, response, status, headers) {
            console.info('onCancelItem', fileItem, response, status, headers);
        };
        uploader.onCompleteItem = function(fileItem, response, status, headers) {
            console.info('onCompleteItem', fileItem, response, status, headers);
        };
        uploader.onCompleteAll = function() {
            console.info('onCompleteAll');
        };

        console.info('uploader', uploader);


 
     

      
    $rootScope.appUrl = "http://localhost/news_app/public";

      var load = function() {
            console.log('call load()...');
              $http.get($rootScope.appUrl+'/post')
                    .success(function(data, status, headers, config) {
                    
                          console.log(data.data);
                     
                        $scope.posts = data.data;
                        $scope.statistiques=data.data_statistique;
                       // $scope.top_posts=data.data_top_posts;

                        //console.log(data.data_statistique);

                        angular.copy($scope.posts, $scope.copy);
                        $scope.sortType     = 'nom'; // set the default sort type
                        $scope.sortReverse  = false;  // set the default sort order
                        $scope.searchFish   = ''; 


                        $scope.viewby = 5;
                        $scope.totalItems = $scope.posts.length;
                        $scope.currentPage = 1;
                        $scope.itemsPerPage = $scope.viewby;
                        $scope.maxSize = 5; //Number of pager buttons to show

                        $scope.setPage = function (pageNo) {
                          $scope.currentPage = pageNo;
                        };

                        $scope.pageChanged = function() {
                          console.log('Page changed to: ' + $scope.currentPage);
                        };

                        $scope.setItemsPerPage = function(num) {
                          $scope.itemsPerPage = num;
                          $scope.currentPage = 1; //reset to first paghe
                        }
                    });
        }

        load();


         $scope.saveFile = function(post_id) {
            $http.post($rootScope.appUrl + '/fichier/add', 

                  {
                     
                    'url':filename,
                    'post_id':post_id
                    
                  }

              ).success(function(data, status, headers, config) {
                        //console.log('success...');
                        load();
                        $location.path('/app/post');
                        toaster.pop('success', 'Message', 'Fichier attachée a bien été ajouté.');
               }).error(function(data, status, headers, config) {
                        toaster.pop('error', 'Message', "Fichier attachée n'a pas été ajouté.");
               }); 
         }





    $scope.oneAtATime = true;

 

    
 

    $scope.status = {
      isFirstOpen: true,
      isFirstDisabled: false
    };
  }])
  ; 
